:root {
  --font-sans: 'Source Sans 3', system-ui, sans-serif;

  --radius: 0.75rem;
  --background: 0 0% 99%;
  --foreground: 222 15% 15%;
  --card: 0 0% 100%;
  --card-foreground: 222 15% 15%;
  --popover: 0 0% 100%;
