@import url('https://fonts.googleapis.com/css2?family=Source+Sans+3:wght@300;400;600;700&display=swap');

:root {
  --font-sans: 'Source Sans 3', system-ui, sans-serif;

  --radius: 0.75rem;
  --background: 0 0% 99%;
  --foreground: 222 15% 15%;
  --card: 0 0% 100%;
  --card-foreground: 222 15% 15%;
  --popover: 0 0% 100%;
  --popover-foreground: 222 15% 15%;
  --primary: 215 85% 35%;
  --primary-foreground: 0 0% 100%;
  --primary-glow: 215 85% 65%;
  --secondary: 210 20% 96%;
  --secondary-foreground: 222 15% 15%;
  --muted: 210 20% 96%;
  --muted-foreground: 222 10% 45%;
  --accent: 215 85% 35%;
  --accent-foreground: 0 0% 100%;
  --success: 142 76% 36%;
  --success-foreground: 0 0% 100%;
  --destructive: 0 84% 60%;
  --border: 220 13% 91%;
  --input: 220 13% 91%;
  --ring: 215 85% 35%;
  --chart-1: 215 85% 35%;
  --chart-2: 142 76% 36%;
  --chart-3: 38 92% 50%;
  --chart-4: 280 70% 50%;
  --chart-5: 340 82% 52%;

  /* Shadows */
  --shadow-elegant: 0 10px 30px -10px hsl(215 85% 35% / 0.25);
  --shadow-subtle: 0 2px 8px -2px hsl(215 20% 60% / 0.15);
  --shadow-glow: 0 0 40px hsl(215 85% 65% / 0.3);

  /* Gradients */
  --gradient-primary: linear-gradient(135deg, hsl(215 85% 35%), hsl(215 85% 50%));
  --gradient-subtle: linear-gradient(180deg, hsl(0 0% 99%), hsl(210 20% 98%));

  /* Animations */
  --transition-smooth: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.dark {
  --background: oklch(0.145 0 0);
  --foreground: oklch(0.985 0 0);
  --card: oklch(0.205 0 0);
  --card-foreground: oklch(0.985 0 0);
  --popover: oklch(0.205 0 0);
  --popover-foreground: oklch(0.985 0 0);
  --primary: oklch(0.922 0 0);
  --primary-foreground: oklch(0.205 0 0);
  --secondary: oklch(0.269 0 0);
  --secondary-foreground: oklch(0.985 0 0);
  --muted: oklch(0.269 0 0);
  --muted-foreground: oklch(0.708 0 0);
  --accent: oklch(0.269 0 0);
  --accent-foreground: oklch(0.985 0 0);
  --destructive: oklch(0.704 0.191 22.216);
  --border: oklch(1 0 0 / 10%);
  --input: oklch(1 0 0 / 15%);
  --ring: oklch(0.556 0 0);
  --chart-1: oklch(0.488 0.243 264.376);
  --chart-2: oklch(0.696 0.17 162.48);
  --chart-3: oklch(0.769 0.188 70.08);
  --chart-4: oklch(0.627 0.265 303.9);
  --chart-5: oklch(0.645 0.246 16.439);
  --sidebar: oklch(0.205 0 0);
  --sidebar-foreground: oklch(0.985 0 0);
  --sidebar-primary: oklch(0.488 0.243 264.376);
  --sidebar-primary-foreground: oklch(0.985 0 0);
  --sidebar-accent: oklch(0.269 0 0);
  --sidebar-accent-foreground: oklch(0.985 0 0);
  --sidebar-border: oklch(1 0 0 / 10%);
  --sidebar-ring: oklch(0.556 0 0);
}

@layer base {
  * {
    @apply border-border outline-ring/50;
  }
  body {
    @apply bg-background text-foreground;
    font-family: var(--font-sans);
    font-weight: 400;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
  }

  h1, h2, h3, h4, h5, h6 {
    font-weight: 700;
    letter-spacing: -0.02em;
  }
}

